import React, { Component } from 'react';
import api from '../../services/api'

import './style.css'

export default class Main extends Component {
    state = {
        projects: [],
        projectInfo: {},
        page: 1,
    }

    componentDidMount() {
        this.loadProducts();
    }

    loadProducts = async (page = 1) => {
        const response = await api.get(`/users?page=${page}`);

        const { docs, ...projectInfo } = response.data;

        this.setState({ projects: docs, projectInfo, page })
    };

    prevPage = () => {
        const { page } = this.state;

        if (page === 1) {
            return;
        }

        const pageNumber = page - 1;

        this.loadProducts(pageNumber);
    }

    nextPage = () => {
        const { page, projectInfo } = this.state;

        if (page === projectInfo.pages) return;
        console.log(projectInfo.pages);
        const pageNumber = page + 1;

        this.loadProducts(pageNumber);
    }

    render() {
        const { projects, page, projectInfo } = this.state;

        return (

            <div class="band">
            {projects.map(project => (
                <div class="item-1">
                    <a href=" " class="card">
                        <img src="https://wallpapercave.com/wp/nkvkU5T.jpg" class="thumb" />
                        <article key={project._id}>
                            <h1>{project.name}</h1>
                            <p>XD</p>
                            <span>{project.name}</span>
                        </article>
                    </a>
                </div>
                ))};
                <div className="actions">
                                <button disabled={page === 1} onClick={this.prevPage}>Anterior</button>
                                <button disabled={page === projectInfo.pages} onClick={this.nextPage}>Proximo</button>
                            </div>
                 <div className="project-list">
                            {projects.map(project => (
                                <article key={project._id}>
                                    <img src="" alt="ENgAGED" />
                                    <br />
                                    <strong>{project.projects}</strong>
                                    <p>por:</p>
                                    <strong>{project.name}</strong>
            
                                    <a href=" ">Ver detalhes</a>
                                </article>
                            ))}
                            
                        </div>

            </div>



            
                       
            

        );
    }
};
