import React, { Component } from 'react';
import api from '../../services/api'

import './style.css'

export default class Main extends Component {
    state = {
        projects: [],
        projectInfo: {},
        page: 1,
    }

    componentDidMount() {
        this.loadProducts();
    }


    loadProducts = async (page = 1) => {
        const response = await api.get(`/products?page=${page}`);

        const { docs, ...projectInfo } = response.data;

        this.setState({ projects: docs, projectInfo, page })
    };

    prevPage = () => {
        const { page } = this.state;

        if (page === 1) {
            return;
        }

        const pageNumber = page - 1;

        this.loadProducts(pageNumber);
    }

    nextPage = () => {
        const { page, projectInfo } = this.state;

        if (page === projectInfo.page) {
            return;
        };

        console.log(page);
        console.log(projectInfo.page);

        const pageNumber = page + 1;

        this.loadProducts(pageNumber);
    }

    render() {
        const { projects } = this.state;

        return (<div className="project-list">
            {projects.map(project => (
                <article key={project._id}>
                    <img src=" " alt="ENgAGED" />
                    <br />
                    <strong>{project.title}</strong>
                    <p>{project.description}</p>
                    <a href=" ">Ver detalhes</a>
                </article>
            ))}
            <div className="actions">
                <button onClick={this.prevPage}>Anterior</button>
                <button onClick={this.nextPage}>Proximo</button>
            </div>
        </div>);
    }
};
